import React from "react";
import Block from "./Block"
// import { Game } from "./Game";
import SpotInOptions from "./Methods"

class Board extends React.Component {
    constructor () {
        super(props)
        this.state = {
          board: this.props.board
        }
    }

    
    shouldComponentUpdate (nextProps) {
      if (nextProps.piece !== this.props.piece)  {
          this.setState({board: nextProps.board})
          return true
      }
      return false;
    }
    

    render () {
      const graphicBoard =  this.state.board.map(row => row.map(piece => <Block key = {piece.spot} piece = {piece} handleClick = {this.handleClick} isOption = {SpotInOptions(piece.spot,this.state.move_options)}/>))
      return (
          <div className="board">
              {graphicBoard}
          </div> 
      )
    }  
}
export default Board
