import React from "react"
import Block from "./Block"


function BuildRow(props) {
    const row = []
    for (let column = 0; column < 8; column++) {
        row.push(<Block piece = {props.board_row[column]} handlePiece = {props.handlePiece}/>)
    }
    return row
}


function BlocksRow(props) {
    let row = new Array(0,0,0,0,0,0,0,0)
    row = row.map((x,index) => <Block piece = {props.board_row[index]} handlePiece = {props.handlePiece}/>)
    return (
        <tr>
            {row}
        </tr>
    )
}

export default BlocksRow
