import React from 'react';
import testUtils from 'react-dom/test-utils';
import BlocksBoard from "./Components/BlocksBoard"
import GameBoard from './Components/GameBoard';

class App extends React.Component {
  constructor () {
    super()
    this.game_board = new GameBoard()
    this.board = this.game_board.board
    this.handlePiece = this.handlePiece.bind(this)
    this.state = {
      piece: " ",
      move_options: []
    }
  }

  handlePiece (piece) {
    console.log(this.board)
    if (this.state.piece == " "){
      const move_options = piece.move_options(this.board) 
      this.setState({piece: piece, move_options: move_options}, () => {
        console.log(this.state)
      })     
    }
    else{
      console.log(this.state.move_options.includes(piece.spot))
      if (true){
        const chosen_spot = [this.state.piece.spot[0],this.state.piece.spot[1]]

        this.board[chosen_spot[0]][chosen_spot[1]].move(piece.spot)
      }
      this.setState({piece:" ", move_options:[]}, () =>{
        console.log(this.state)
      })
    }
  }
  //   this.setState({
  //       piece_clicked: piece,
  //       move_options: piece.move_options(this.board)
  //     })
  //   console.log(this.state)

  IsSpotInOptions (spot) {
    for (let index = 0; index < this.state.move_options.length; index++) {
      const element = this.state.move_options[index];
      if (element[0] === spot[0] && element[1] === spot[1]){
        return true
      }
    }
    return false
  }


  render () {
    return(
      <div>
         <BlocksBoard board = {this.board} handlePiece = {this.handlePiece}/>
      </div>
    )
  }
}

export default App;
