import { Piece, Pawn, Rook, Bishop, Queen, King, Knight} from "./pieces"
import SpotInOptions from "./Methods"

export class Game {
    constructor() {
        this.turn = 'w'
        this.whiteKing = new King([7,4],'K','w')
        this.blackKing = new King([0,4],'K','b')
        this.board = this.createBoard()
    }

    createBoard () {
        const board = []
        for (let rowindex = 0; rowindex < 8; rowindex++) {
            const row = []
            for (let columnindex = 0; columnindex < 8; columnindex++) {
                let p = new Piece([rowindex,columnindex],'0','e',"")
                row.push(p);
            }
            board.push(row)
        }
        board[6] = board[6].map((x,index) => new Pawn([6,index],'P','w'))
        board[1] = board[1].map((x,index) => new Pawn([1,index],'P','b'))
        board[7][4] = this.whiteKing
        board[0][4] = this.blackKing
        board[7][3] = new Queen([7,3],'Q','w')
        board[0][3] = new Queen([0,3],'Q','b')
        board[7][0] = new Rook([7,0],'R','w')
        board[7][7] = new Rook([7,7],'R','w')
        board[0][0] = new Rook([0,0],'R','b')
        board[0][7] = new Rook([0,7],'R','b')
        board[7][2] = new Bishop([7,2],'B','w')
        board[7][5] = new Bishop([7,5],'B','w')
        board[0][2] = new Bishop([0,2],'B','b')
        board[0][5] = new Bishop([0,5],'B','b')
        board[7][1] = new Knight([7,1],'k','w')
        board[7][6] = new Knight([7,6],'k','w')
        board[0][1] = new Knight([0,1],'k','b')
        board[0][6] = new Knight([0,6],'k','b')
        return board;
    } 
    
    update (chosen_spot, piece) {
        this.board[chosen_spot[0]][chosen_spot[1]] = piece;
        this.board[piece.spot[0]][piece.spot[1]] = new Piece(piece.spot,'0','e');
    }

    updateTurn () {
        if (this.turn === 'w'){
            this.turn = 'b'
        } else{
            this.turn = 'w'
        }
    }

    isCheck () {
        for (let row = 0; row < this.board.length; row++) {
            for (let column = 0; column < this.board[row].length; column++) {
                const piece = this.board[row][column]
                const move_options = piece.move_options(this.board)
                if (this.turn === 'w') {
                    if (SpotInOptions(this.whiteKing.spot,move_options)) {
                        return true
                    }
                }
                else if (SpotInOptions(this.blackKing.spot,move_options)){
                    return true
                }
            }
        }
        return false
    }
    
    getAvaliableMoves(piece,move_options) {
        let i = 0
        let option
        let pieceMoving
        let copyGame
        while (i < move_options.length) {
            option = move_options[i]
            copyGame = new Copy_Game(this)
            pieceMoving = copyGame.board[piece.spot[0]][piece.spot[1]]
            console.log(pieceMoving)
            pieceMoving.move(option,copyGame)
            if (copyGame.isCheck()) {
                move_options.splice(i,i)
                i -= 1
            }
            i += 1
        }
        return move_options
    }
}

export class Copy_Game extends Game {
    constructor (game) {
        super()
        this.turn = game.turn
        this.whiteKing = game.whiteKing
        this.blackKing = game.blackKing
        this.board = this.createCopyBoard(game.board)
    }
    createCopyBoard (board) {
        const copy_board = []
        for (let rowindex = 0; rowindex < 8; rowindex++) {
            const row = []
            for (let columnindex = 0; columnindex < 8; columnindex++) {
                let p = {... board[rowindex][columnindex]}
                row.push(p);
            }
            copy_board.push(row)
        }
        return copy_board
    }
}
