import React from "react"

const Timer = (props) => {
    const time = ""
    const minutes = props.time/60
    const second = props.time % 60
    if (minutes < 10) {
        time += "0" + minutes + ":"
    }
    else {
        time += minutes + ":"
    }
    if (second < 10) {
        time += "0" + second
    }
    else {
        time += second
    }

    return (
        <div className="timer">{time}</div>
    )
}

export default Timer
