import React from "react"

const Block = (props) => {
    const piece = props.piece
    const src = ".\\Chesspieceses\\" + piece.img
    if ((piece.spot[0] + piece.spot[1]) % 2 === 0) {
        return(
            <td className="color0" onClick = {() => props.handlePiece(piece)}><img alt="" className="IMG" src= {src}></img></td>
        )
    }
    else{
        return(
            <td className="color1" onClick = {() => props.handlePiece(piece)}><img alt="" className="IMG" src= {src}></img></td>
        )
    }
}

export default Block
