import { Piece , Pawn}  from "./pieces"


class GameBoard {
    constructor() {
        this.board = this.createBoard()
    }
    
    createBoard () {
        const board = []
        for (let rowindex = 0; rowindex < 8; rowindex++) {
            const row = []
            for (let columnindex = 0; columnindex < 8; columnindex++) {
                let p = new Piece([rowindex,columnindex],'0','e')
                row.push(p);
            }
            board.push(row)
        }

        board[6] = board[6].map((x,index) => new Pawn([6,index],'P','w'))
        board[1] = board[1].map((x,index) => {
            return new Pawn([1,index],'P','w')
        })
        return board;
    }   
}

export default GameBoard
