import React from "react";
import Block from "./Block"
import { Game } from "./Game";

class Board extends React.Component {
    constructor () {
        super()
        this.state = {
          board: this.props.board
        }
    }

    

      IsSpotInOptions = (spot,move_options) => {
        for (let index = 0; index < move_options.length; index++) {
          const element = move_options[index];
          if (element[0] === spot[0] && element[1] === spot[1]) {
            return true
          }
        }
        return false
      }
    

      render () {
        const graphicBoard =  this.state.board.map(row => row.map(piece => <Block key = {piece.spot} piece = {piece} handleClick = {this.handleClick} isOption = {this.IsSpotInOptions(piece.spot,this.state.move_options)}/>))
        return (
            <div className="board">
                {graphicBoard}
            </div> 
        )
      }  
}
export default Board
