import React from "react"



class Timer extends React.Component {
    constructor (props) {
        super()
        this.state = {
            time: 900
        }
    }
    componentDidMount() {
        this.myInterval = setInterval(() => {
          this.setState(prevState => {
              return {time: prevState.time - 1}
          })
        }, 1000)
      }
    
    render () {
        let time = ""
        const minutes = this.state.time / 60
        alert(minutes)
        const seconds = this.state.time % 60
        alert(seconds)
        if (minutes < 10) {
            time += "0" + minutes + ":"
        }
        else {
            time += minutes + ":"
        }
        if (seconds < 10) {
            time += "0" + seconds
        }
        else {
            time += seconds
        }
    
        return (
            <div className="timer">{time}</div>
        )
    }
}

export default Timer
