import React from "react"
import GameBoard from "./GameBoard"


class Block extends React.Component {

    constructor (props){
        super()
        this.piece = props.piece
        this.HandleClick = this.HandleClick.bind(this)
    }

    HandleClick () {
        alert(this.piece.spot)
    }

    render (props) {
        
        const src = ".\\Chesspieceses\\" + this.piece.img
        if ((this.piece.spot[0] + this.piece.spot[1]) % 2 === 0) {
            return(
                <td className="color0" onClick = {() => props.ReturnMoveOptions(this)}><img alt="" className="IMG" src= {src}></img></td>
            )
        }
        else{
            return(
                <td className="color1"><img alt="" className="IMG" src= {src}></img></td>
            )
        }
    }
}


export default Block
