import React from "react"


// function Block(props){
    
    
// }

class Block extends React.Component {

    constructor (props){
        super()
        console.log(props)
        this.column = props.data.column
        this.row = props.data.row
        this.img = <img src=".\Chesspieces\BlackKing.png"></img>
    }
    render () {
        if ((this.row + this.column) % 2 == 0) {
            return(
                <td className="color0"><img alt="" className="IMG" src=".\Chesspieceses\WhiteKing.png"></img></td>
            )
        }
        else{
            return(
                <td className="color1"><img alt="" className="IMG" src=".\Chesspieceses\WhiteKing.png"></img>/</td>
            )
        }
    }
}


export default Block
