import React from "react";
import BlocksRow from "./BlocksRow";

function image_board(){
    const image_board = []
    for (let row = 0; row < 8; row++) {
        const row_images = []
        for (let column = 0; column < 8; column++) {
            row_images.push[""];
        }
        image_board.push[row_images]
    }
    image_board[7][4] = "WhiteKing.png"
    image_board[7][3] = "WhiteQueen.png"
    image_board[7][0] = "WhiteRook.png"
    image_board[7][7] = "WhiteRook.png"
    image_board[7][2] = "WhiteBishop.png"
    image_board[7][5] = "WhiteBishop.png"
    image_board[7][1] = "WhiteKnight.png"
    image_board[7][6] = "WhiteKnight.png"

    image_board[0][4] = "BlackKing.png"
    image_board[0][3] = "BlackQueen.png"
    image_board[0][0] = "BlackRook.png"
    image_board[0][7] = "BlackRook.png"
    image_board[0][2] = "BlackBishop.png"
    image_board[0][5] = "BlackBishop.png"
    image_board[0][1] = "BlackKnight.png"
    image_board[0][6] = "BlackKnight.png"

    for (let i = 0; i < 8; i++) {
        image_board[6][i] = "WhitePawn.png"
        image_board[1][i] = "BlackPawn.png"
    }

    return image_board
}

function Board () {
    const image_b = image_board()
    console.log(image_b)
    const board = []
    for (let row = 0; row < 8; row++) {
        board.push(<BlocksRow row = {row} image_board = {image_b}/>)        
    }
    
    return (
        <div>
            <table className="center">
                {board}
            </table>
        </div>
    )
    
}

export default Board
