import React from "react"

const picture  = {'w' : { 'P': "WhitePawn.png",
                         'R': "WhiteRook.png",
                         'B': "WhiteBishop.png",
                        'k': "WhiteKnight.png",
                        'Q': "WhiteQueen.png",
                        'K': "WhiteKing.png" 
                    },
                'b' : { 'P': "BlackPawn.png",
                        'R': "BlackRook.png",
                        'B': "BlackBishop.png",
                        'k': "BlackKnight.png",
                        'Q': "BlackQueen.png",
                        'K': "BlackKing.png"
                    }
}

const Block = (props) => {
    const piece = props.piece
    const picColor = picture[piece.color]
    const src = ""
    console.log(piece.lett)
    // const src = ".\\Chesspieceses\\" + picColor[piece.lett]
    let className
    if ((piece.spot[0] + piece.spot[1]) % 2 === 0 ) {
        className = "block white"
    } else {
        className = "block black"
    }
    return (
        <div className={className} onClick={() => props.handlePiece(piece)}>
            <img className="piece" src={src} alt=""></img>
        </div>
    )
}
export default Block
