import React from "react"



class Timer extends React.Component {

    componentDidMount() {
        this.myInterval = setInterval(() => {
          this.setState(prevState => {
            if (prevState.game.turn) {
              return {wTime: prevState.wTime - 1}
            }
            else{
              return {bTime: prevState.wTime - 1}
            }
          })
        }, 1000)
      }
    
    render () {
        let time = ""
        const minutes = props.time/60
        const second = props.time % 60
        if (minutes < 10) {
            time += "0" + minutes + ":"
        }
        else {
            time += minutes + ":"
        }
        if (second < 10) {
            time += "0" + second
        }
        else {
            time += second
        }
    
        return (
            <div className="timer">{time}</div>
        )
    }
}

export default Timer
