import React from "react";
import Block from "./Block"
import Game from "./Game";

class Board extends React.Component {
    constructor () {
        super()
        // this.game = new Game()

        this.state = {
          piece: null,
          game: new Game()

          // graphicBoard: this.game.board.map(row => row.map(piece => <Block key = {piece.spot} piece = {piece} handlePiece = {this.handlePiece} />))
        }
    }
    handlePiece = (piece) => {
        const row = piece.spot[0]
        const column = piece.spot[1]
        this.setState(prevState => {
          if (this.state.piece == null) {
              const move_options = piece.move_options(this.state.game.board) 
              if (move_options.length > 0) { 
                return {piece: piece, game: prevState.game}
              }
            }
          else{
            // let graphicBoard = prevState.graphicBoard
            if (this.IsSpotInOptions(piece.spot, prevState.piece.move_options(this.state.game.board))) {

              
              this.state.game.board[prevState.piece.spot[0]][prevState.piece.spot[1]].move([row,column],this.state.game)
              console.log(this.state.game.board)
              // graphicBoard = prevState.graphicBoard.map(row => row.map(Block => {
              //   console.log(Block.key)
              //   if (Block.key === String(piece.spot)) {
              //     // Block.props.piece = prevState.piece
              //   }
              //   return Block
              // }))
            }
            return {piece: null, game: this.state.game}
          }
        })
      }

      IsSpotInOptions = (spot,move_options) => {
        for (let index = 0; index < move_options.length; index++) {
          const element = move_options[index];
          if (element[0] === spot[0] && element[1] === spot[1]) {
            return true
          }
        }
        return false
      }

      render () {
        const graphicBoard =  this.state.game.board.map(row => row.map(piece => <Block key = {piece.spot} piece = {piece} handlePiece = {this.handlePiece} />))
        return (
            <div className="board">
                {graphicBoard}
            </div> 
        )
      }  
}
export default Board
