    render () {
      alert("hi")
      const graphicBoard =  this.state.board.map(row => row.map(piece => <Block key = {piece.spot} piece = {piece} handleClick = {this.props.handleClick} isOption = {SpotInOptions(piece.spot,this.state.move_options)}/>))
      return (
          <div className="board">
              {graphicBoard}
          </div> 
      )
    }  
}
export default Board
