const Block = (props) => {
    const piece = props.piece
    const src = ".\\Chesspieceses\\" + piece.img
    const id = (piece.spot[0] + piece.spot[1]) % 2
    const className = "color" + id
    return (
        <button className={className} oncick = {() => props.handlePiece(piece)}>
            <img alt={piece.lett} src={src} className={className}></img>
        </button>
    )
}

export default Block
