import React from "react";
import BlocksRow from "./BlocksRow";

function image_board(){
    const image_board = []
    for (let row = 0; row < 8; row++) {
        const row_images = []
        for (let column = 0; column < 8; column++) {
            row_images.push[""];
        }
        image_board.push[row_images]
    }
    image_board[7][4] = self.white_king
    image_board[7][3] = queen("w",(white_row,3), "Q")
    image_board[7][0] = rook("w", (white_row, 0), "R")
    image_board[7][7] = rook("w", (white_row, 7), "R")
    image_board[7][2] = bishop("w", (white_row, 2), "B")
    image_board[7][5] = bishop("w", (white_row, 5), "B")
    image_board[7][1] = knight("w" ,(white_row,1), "k")
    image_board[7][6] = knight("w", (white_row,6), "k")

    image_board[black_row][4] = self.black_king
    image_board[black_row][3] = queen("b",(black_row,3), "Q")
    image_board[black_row][0] = rook("b", (black_row, 0), "R")
    image_board[black_row][7] = rook("b", (black_row, 7), "R")
    image_board[black_row][2] = bishop("b", (black_row, 2), "B") 
    image_board[black_row][5] = bishop("b", (black_row, 5), "B")
    image_board[black_row][1] = knight("b",(black_row,1), "k")
    image_board[black_row][6] = knight("b",(black_row,6), "k")

    for (let i = 0; i < 8; i++) {
        board[6][i] = pawn("w",(6,i), "P")
        board[1][i] = pawn("b",(1,i), "P")
    }

    return image_board
}
function Board () {
    
    const board = []
    for (let row = 0; row < 8; row++) {
        board.push(<BlocksRow row = {row}/>)        
    }
    
    return (
        <div>
            <table className="center">
                {board}
            </table>
        </div>
    )
    
}

export default Board
