import React from "react";
import Block from "./Block"
import Game from "./Game";

class Board extends React.Component {

    constructor () {
        super()
        this.game = new Game()
        this.state = {
          piece: " ",
          move_options: []
        }
        this.graphicBoard = this.game.board.map(row => row.map(piece => <Block key = {piece.spot} piece = {piece} handlePiece = {this.handlePiece} />))
    }

    handlePiece = (piece) => {

        if (this.state.piece === " ") {
          const move_options = piece.move_options(this.game.board) 
          if (move_options.length > 0 ) {
            this.setState({piece: piece, move_options: move_options})
          }
        }
        else {
          if (this.IsSpotInOptions(piece.spot,this.state.move_options)) {
            this.game.board[this.state.piece.spot[0]][this.state.piece.spot[1]].move(piece.spot, this.game.board)
            this.graphicBoard[piece.spot[0]][piece.spot[1]] = <Block key = {piece.spot} piece = {piece} handlePiece = {this.handlePiece} />
          }
          this.setState({piece:" ", move_options:[]})
        }
      }

      IsSpotInOptions = (spot,move_options) => {
        for (let index = 0; index < move_options.length; index++) {
          const element = move_options[index];
          if (element[0] === spot[0] && element[1] === spot[1]) {
            return true
          }
        }
        return false
      }

      render () {
        return (
<           div className="board">
                {this.graphicBoard}
            </div> 
        )
      }  
}
export default Board
