import React from 'react';
import BlocksBoard from "./Components/BlocksBoard"
import GameBoard from './Components/GameBoard';

class App extends React.Component {
  constructor () {
    super()
    this.game_board = new GameBoard()
    this.board = this.game_board.board
    // this.handlePiece = this.handlePiece.bind(this)
    // this.state = {
    //   piece_clicked = false
    // }
  }

  handlePiece = (piece) => {
    console.log(piece.move_options(this.board))
  }
  render () {
    return(
      <div>
         <BlocksBoard board = {this.board} handlePiece = {this.handlePiece}/>
      </div>
    )
  }
}

export default App;
