import React from "react"



class Timer extends React.Component {
    constructor () {
        super(props)
        this.state = {
            time: props.time
        }
    }
    componentDidMount() {
        this.myInterval = setInterval(() => {
          this.setState(prevState => {
              return {time: prevState.wTime - 1}
          })
        }, 1000)
      }
    
    render () {
        let time = ""
        const minutes = this.state.time/60
        const second = this.state.time % 60
        if (minutes < 10) {
            time += "0" + minutes + ":"
        }
        else {
            time += minutes + ":"
        }
        if (second < 10) {
            time += "0" + second
        }
        else {
            time += second
        }
    
        return (
            <div className="timer">{time}</div>
        )
    }
}

export default Timer
