import React from "react"


// function Block(props){
    
    
// }

class Block extends React.Component {

    constructor (props){
        console.log(props)
        super()
        this.column = props.data.column
        this.row = props.data.row
        this.img = props.data.img
    }

    HandleClick () {
        console.log("Hi")
    }

    render () {
        
        const src = ".\\Chesspieceses\\" + this.img
        console.log(src)
        if ((this.row + this.column) % 2 === 0) {
            return(
                <td className="color0" onClick = {HandleClick(this)}><img alt="" className="IMG" src= {src}></img></td>
            )
        }
        else{
            return(
                <td className="color1"><img alt="" className="IMG" src= {src}></img></td>
            )
        }
    }
}


export default Block
