import React from 'react';
import BlocksBoard from "./Components/BlocksBoard"
import GameBoard from './Components/GameBoard';

class App extends React.Component {
  constructor () {
    super()
    this.game_board = new GameBoard()
    this.board = this.game_board.board
    this.state = {
      piece_clicked: null,
      move_options: null
    }
  }

  handlePiece = (piece) => {
    if (this.state.piece == null){
      const move_options = piece.move_options(this.board) 
      this.setState({
        piece_clicked: piece,
        move_options: move_options
      })
      console.log(this.state)
    }
    else{
      if (this.state.move_options.includes(piece.spot)){
        const chosen_spot = [this.state.piece.spot[0],this.state.piece.spot[1]]
        this.board[chosen_spot[0]][chosen_spot[1]].move(piece.spot)
      }
      this.state = this.setState({piece_clicked:null, move_options:null })
    }

  }

  render () {
    return(
      <div>
         <BlocksBoard board = {this.board} handlePiece = {this.handlePiece}/>
      </div>
    )
  }
}

export default App;
