import React from "react";
import Block from "./Block"
import { Game} from "./Game";

class Board extends React.Component {
    constructor () {
        super()
        this.state = {
          piece: null,
          game: new Game(),
          move_options: []
        }
    }
    handleClick = (piece) => {
        const row = piece.spot[0]
        const column = piece.spot[1]
        this.setState(prevState => {
          if (this.state.piece == null) {
            if (this.state.game.turn === piece.color) {
              const move_options = piece.getAvaliableMoves(prevState.game)
              if (move_options.length > 0) { 
                return {piece: piece, game: prevState.game, move_options: move_options}
              }
            }
          }
          else{
            if (this.IsSpotInOptions(piece.spot, this.state.move_options)) {
              prevState.game.board[prevState.piece.spot[0]][prevState.piece.spot[1]].move([row,column],this.state.game)
              prevState.game.updateTurn()
              this.updateStatus(prevState.game)
            }
            return {piece: null, game: prevState.game, move_options: []}
          }
        })
      }

      IsSpotInOptions = (spot,move_options) => {
        for (let index = 0; index < move_options.length; index++) {
          const element = move_options[index];
          if (element[0] === spot[0] && element[1] === spot[1]) {
            return true
          }
        }
        return false
      }
      
      updateStatus = (game) => {
        if (game.isCheck()) {
          if (game.isCheckMate()) {
            this.props.updateStatus("CHECKMATE!")
          }
          else{
            this.props.updateStatus("CHECK!")
          }
        }
        else if (game.isPat()){
          this.props.updateStatus("PAT!")
        }
        else{
          this.props.updateStatus("")
        }
      }

      render () {
        const graphicBoard =  this.state.game.board.map(row => row.map(piece => <Block key = {piece.spot} piece = {piece} handleClick = {this.handleClick} isOption = {this.IsSpotInOptions(piece.spot,this.state.move_options)}/>))
        return (
            <div className="board">
                {graphicBoard}
            </div> 
        )
      }  
}
export default Board
